live.del_flag = '0' AND live.live_end_time > CURRENT_TIMESTAMP() AND ((live.live_branch = #{branchId,jdbcType=VARCHAR} AND live.live_scope = "LIVE_LOCAL_BRANCH") OR (live.live_hospital = #{hospitalId,jdbcType=VARCHAR} AND live.live_scope = "LIVE_LOCAL_HOSPITAL") OR (customBranch.branch_id = #{sameBranchId,jdbcType=VARCHAR} AND live.live_scope = "LIVE_SAME_BRANCH") OR (live.live_scope = "LIVE_ALL_HOSPITAL"))
AND live.live_type = #{liveType,jdbcType=VARCHAR}

select live.id id, live_id, live_name, live_number, live_cover_url, live_description, live_start_time, live_end_time, live_branch, live_password, live_type, live_period, live_url, live_scope, live_user, live_mute, live_record, live_start, live_json, customBranch.custom_name liveBranchName,hospital.hospital_name liveHospitalName from _live live LEFT JOIN custom_branch customBranch ON customBranch.id = live.live_branch LEFT JOIN base_hospital hospital ON hospital.id = live.live_hospital
