mapper.select:
select count(id) num from video_on_demand
del_flag = '0' AND trans_code = '1'
AND video_type = #{videoType,jdbcType=VARCHAR}

select vod.id vodId,vod.video_name videoName,vod.video_describe videoDescribe, vod.video_file_id videoFileId, vod.video_cover_url videoCoverUrl,vod.playback playback,vod.trans_code transCode,vod.upload_time uploadTime, hospital.hospital_name hospitalName, branch.branch_name branchName from video_on_demand vod LEFT JOIN base_hospital hospital ON hospital.id = vod.hospital_id LEFT JOIN base_branch branch ON branch.id = vod.branch_id
vod.del_flag = '0' AND vod.create_user = #{createUser,jdbcType=VARCHAR}
order by vod.create_time DESC limit #{beginNo,jdbcType=VARCHAR}, #{pageSize,jdbcType=VARCHAR}

mapper.delete:
delete from video_on_demand
where id = #{id,jdbcType=VARCHAR}
AND create_user = #{createUser,jdbcType=VARCHAR}
AND video_file_id = #{videoFileId,jdbcType=VARCHAR}
