    and apply_summary = #{applySummary,jdbcType=VARCHAR}


    and invite_summary = #{inviteSummary,jdbcType=VARCHAR}


    and apply_user_id = #{applyUserId,jdbcType=VARCHAR}


    and apply_hospital_id = #{applyHospitalId,jdbcType=VARCHAR}


    and invite_user_id = #{inviteUserId,jdbcType=VARCHAR}


    and invite_hospital_id = #{inviteHospitalId,jdbcType=VARCHAR}


    and invite_branch_id = #{inviteBranchId,jdbcType=VARCHAR}


    and apply_branch_id = #{applyBranchId,jdbcType=VARCHAR}


    and consultant_apply_time = #{consultantApplyTime,jdbcType=TIMESTAMP}


    and apply_status = #{applyStatus,jdbcType=VARCHAR}


    and apply_type = #{applyType,jdbcType=VARCHAR}


    and apply_remark = #{applyRemark,jdbcType=VARCHAR}


    and refuse_remark = #{refuseRemark,jdbcType=VARCHAR}


    and apply_urgent = #{applyUrgent,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and update_user = #{updateUser,jdbcType=VARCHAR}


    and delete_time = #{deleteTime,jdbcType=TIMESTAMP}


    and delete_user = #{deleteUser,jdbcType=VARCHAR}

order by update_time desc,create_time desc
limit #{beginNo},#{pageSize}

