select count(af.id) afNumber, applybh.hospital_name applyHospital, invitebh.hospital_name inviteHospital, applycb.custom_name applyBranch, invitecb.custom_name inviteBranch, applyud.title_name applyName, inviteud.title_name inviteName from apply_form af left join base_hospital applybh on af.apply_hospital_id = applybh.id left join base_hospital invitebh on af.invite_hospital_id = invitebh.id left join custom_branch applycb on af.apply_branch_id = applycb.id left join custom_branch invitecb on af.invite_branch_id = invitecb.id left join user_detail applyud on af.apply_user_id = applyud.id left join user_detail inviteud on af.invite_user_id = inviteud.id where af.del_flag = "0" and #{startTime}<af.consultant_apply_time <#{endTime}
and af.apply_hospital_id=#{hospitalId}
and af.invite_hospital_id=#{hospitalId}
and af.apply_status = #{status} group by ${queryCondition1}
,${queryCondition2}

select count(af.id) afNumber,DATE_FORMAT(af.consultant_apply_time, #{dayMouthYear}) dates
from apply_form af
left join case_consultant cc on af.id = cc.id
where af.del_flag = "0"
and af.apply_status = #{status}
and #{startTime}<af.consultant_apply_time <#{endTime}

    and af.apply_hospital_id=#{hospitalId}


    and af.invite_hospital_id=#{hospitalId}


    and af.apply_type=#{picture}


    and af.apply_type=#{video}


    and mdt_flag = '1'


    and mdt_flag = '0'

group by dates
