mapper.select:
select case_summary, apply_summary, invite_summary,consultant_apply_time,apply_status,case_record_id,
apply_type, apply_remark,apply_urgent, create_time,update_time
from apply_form

    del_flag = "0"

        AND apply_type IN

            #{item,jdbcType=VARCHAR}



        AND apply_status IN

            #{item,jdbcType=VARCHAR}



        AND (apply_user_id = #{applyUserId,jdbcType=VARCHAR}


        or invite_user_id = #{inviteUserId,jdbcType=VARCHAR})


order by update_time,create_time
limit #{beginNo},#{pageSize}

