mapper.select:
select af.id afId,af.case_summary afCaseSummary, af.apply_summary afApplySummary, af.invite_summary afInviteSummary, af.consultant_apply_time afConsultantApplyTime,af.apply_status afApplyStatus,af.case_record_id afCaseRecordId, af.apply_type afApplyType, af.apply_remark afApplyRemark,af.apply_urgent afApplyUrgent, af.create_time afCreateTime,af.update_time afUpdateTime from case_consultant cc left join apply_form af on af.id = cc.id and af.del_flag = "0"
af.apply_type IN
#{item,jdbcType=VARCHAR}
AND af.apply_status IN
#{item,jdbcType=VARCHAR}
and (cc.invite_user_id = #{inviteUserId,jdbcType=VARCHAR} or cc.consultant_user_list like CONCAT('%',#{inviteUserId},'%'))
order by af.apply_urgent desc ,af.update_time,af.create_time limit #{beginNo},#{pageSize}
