mapper.select:
SELECT applyHospital.hospital_name applyHospitalName, inviteHospital.hospital_name inviteHospitalName, applyCustomBranch.custom_name applyCustomBranchName, inviteCustomBranch.custom_name inviteCustomBranchName, applyUserSpecialistType.specialist_name applyUserSpecialistTypeName, inviteUserSpecialistType.specialist_name inviteUserSpecialistTypeName, count(*) AS groupCount FROM apply_form applyForm LEFT JOIN base_hospital applyHospital ON applyHospital.id = applyForm.apply_hospital_id LEFT JOIN base_hospital inviteHospital ON inviteHospital.id = applyForm.invite_hospital_id LEFT JOIN custom_branch applyCustomBranch ON applyCustomBranch.id = applyForm.apply_branch_id LEFT JOIN custom_branch inviteCustomBranch ON inviteCustomBranch.id = applyForm.invite_branch_id LEFT JOIN user_detail applyUserDetail ON applyUserDetail.id = applyForm.apply_user_id LEFT JOIN specialist_type applyUserSpecialistType ON applyUserSpecialistType.id = applyUserDetail.specialist_type_id LEFT JOIN user_detail inviteUserDetail ON inviteUserDetail.id = applyForm.invite_user_id LEFT JOIN specialist_type inviteUserSpecialistType ON inviteUserSpecialistType.id = inviteUserDetail.specialist_type_id WHERE applyForm.del_flag = "0" AND #{startTime}<applyForm.consultant_apply_time <#{endTime}
AND applyForm.apply_hospital_id = #{hospitalId}
AND applyForm.invite_hospital_id = #{hospitalId}
AND applyForm.apply_status = #{applyStatus} GROUP BY
applyForm.apply_branch_id;
applyForm.invite_branch_id;
applyUserSpecialistType.id;
inviteUserSpecialistType.id;
