mapper.select:
select

from tencent_video video
LEFT JOIN custom_branch customBranch ON customBranch.id = video.dept_id
LEFT JOIN base_hospital hospital ON hospital.id = customBranch.hospital_id

    video.del_flag = #{delFlag,jdbcType=CHAR}
    AND video_class = #{videoClass,jdbcType=VARCHAR}
    AND trans_code = '1'

ORDER BY playback DESC
limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
count(id) num
from tencent_video video

    video.del_flag = #{delFlag,jdbcType=CHAR}
    AND video_class = #{videoClass,jdbcType=VARCHAR}
    AND trans_code = '1'

select

from tencent_video video
LEFT JOIN custom_branch customBranch ON customBranch.id = video.dept_id
LEFT JOIN base_hospital hospital ON hospital.id = customBranch.hospital_id

    video.del_flag = #{delFlag,jdbcType=CHAR}
    AND video_class = #{videoClass,jdbcType=VARCHAR}
    AND trans_code = '1'

ORDER BY upload_time DESC
limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
